/*

	pw_client.js

	This is how the server its clients.
*/

PW_Client = function (data) {

	"use strict";
	// ID should be facbook's user ID
	this.id = data.id;

	// right now name is equal to ID
	// but in the future it should be
	// user's full name taken from facebook
	this.name = data.name;

	this.socket = data.socket;

	this.gameStatus = GAME.STATUS.IDLE;

	this.getID = function () { return this.id; };

	this.getName = function () { return this.name; };

	this.getGameStatus = function () { return this.gameStatus; };

	this.setGameStatus = function (status) { this.gameStatus = status; };

	this.getSocket = function () { return this.socket; };

};

module.exports.PW_Client = PW_Client;
